mapper.select:
SELECT admission.admission_nm, exam.type_nm, exam.exam_date, exam.exam_time, examinee.college_nm, examinee.dept_nm, examinee.major_nm, exam_map.group_nm, exam.exam_nm, hall.head_nm, hall.bldg_nm, hall.hall_nm, examinee.examinee_cd, examinee.examinee_nm, exam_map.virt_no, COUNT(score.scorer_nm) AS scorer_cnt, SUM(score.score01) / COUNT(score.scorer_nm) AS avg_score01, SUM(score.score02) / COUNT(score.scorer_nm) AS avg_score02, SUM(score.score03) / COUNT(score.scorer_nm) AS avg_score03, SUM(score.score04) / COUNT(score.scorer_nm) AS avg_score04, SUM(score.score05) / COUNT(score.scorer_nm) AS avg_score05, SUM(score.score06) / COUNT(score.scorer_nm) AS avg_score06, SUM(score.score07) / COUNT(score.scorer_nm) AS avg_score07, SUM(score.score08) / COUNT(score.scorer_nm) AS avg_score08, SUM(score.score09) / COUNT(score.scorer_nm) AS avg_score09, SUM(score.score10) / COUNT(score.scorer_nm) AS avg_score10,
SUM(score.score01) AS tot_score01, SUM(score.score02) AS tot_score02, SUM(score.score03) AS tot_score03, SUM(score.score04) AS tot_score04, SUM(score.score05) AS tot_score05, SUM(score.score06) AS tot_score06, SUM(score.score07) AS tot_score07, SUM(score.score08) AS tot_score08, SUM(score.score09) AS tot_score09, SUM(score.score10) AS tot_score10,
SUM(score.score01 + score.score02 + score.score03 + score.score04 + score.score05 + score.score06 + score.score07 + score.score08 + score.score09 + score.score10) AS total_score, CASE WHEN exam_map.virt_no IS NULL THEN FALSE WHEN COUNT(score.scorer_nm) = 0 THEN FALSE WHEN (SELECT COUNT(*) FROM score WHERE exam_map.exam_cd = score.exam_cd AND exam_map.virt_no = score.virt_no AND score.total_score = '결시') > 0 THEN FALSE ELSE TRUE END AS is_attend, now() AS print_dttm FROM exam_map INNER JOIN exam ON exam_map.exam_cd = exam.exam_cd INNER JOIN hall ON exam_map.hall_cd = hall.hall_cd INNER JOIN examinee ON exam_map.examinee_cd = examinee.examinee_cd INNER JOIN admission ON exam.admission_cd = admission.admission_cd LEFT JOIN score ON exam_map.exam_cd = score.exam_cd AND exam_map.virt_no = score.virt_no
AND admission.admission_nm = #{param.admissionNm} AND exam.type_nm = #{param.typeNm} AND exam.exam_date = #{param.examDate} AND exam.exam_time = DATE_FORMAT(#{param.examTime}, '%T') AND examinee.dept_nm = #{param.deptNm} AND examinee.major_nm = #{param.majorNm} AND exam_map.group_nm = #{param.groupNm} AND hall.head_nm = #{param.headNm} AND hall.bldg_nm = #{param.bldgNm} AND hall.hall_nm = #{param.hallNm} AND exam_map.virt_no = #{param.virtNo}
AND exam_map.virt_no IS NOT NULL AND score.score01 IS NOT NULL AND exam_map.virt_no IS NULL AND score.score01 IS NULL
AND examinee.examinee_cd LIKE '%' #{param.examineeCd} '%' AND examinee.examinee_nm LIKE '%' #{param.examineeNm} '%' AND examinee.examinee_cd = #{param.fromExamineeCd} AND examinee.examinee_cd = #{param.toExamineeCd}
AND examinee.examinee_cd BETWEEN #{param.fromExamineeCd} AND #{param.toExamineeCd}
GROUP BY admission.admission_nm , exam.exam_date , exam.exam_time , examinee.dept_nm , examinee.major_nm , exam.exam_nm , hall.head_nm , hall.bldg_nm , hall.hall_nm , exam_map.group_nm , examinee.examinee_cd , examinee.examinee_nm

SELECT admission.admission_nm
    , exam.type_nm
    , exam.exam_date
    /*, exam.exam_time*/
    , date_format(exam_time,'%H:%i') as exam_time
    , examinee.dept_nm
    , examinee.major_nm
    , examinee.examinee_nm
    , exam_map.exam_cd
    , exam_map.virt_no
    , date_format(scan_dttm,'%Y-%m-%d %H:%i:%s') as scan_dttm
    , exam_map.examinee_cd
    , exam_map.group_nm
    , hall.head_nm
    , hall.bldg_nm
    , hall.hall_nm
    , (SELECT SUM(score01) FROM score WHERE score.virt_no = exam_map.virt_no) AS total
FROM exam_map
    INNER JOIN exam ON exam_map.exam_cd = exam.exam_cd
    INNER JOIN admission ON exam.admission_cd = admission.admission_cd
    INNER JOIN examinee ON exam_map.examinee_cd = examinee.examinee_cd
    INNER JOIN hall ON exam_map.hall_cd = hall.hall_cd


        AND admission.admission_nm = #{param.admissionNm}
        AND exam.type_nm = #{param.typeNm}
        AND exam.exam_date = #{param.examDate}
        AND exam.exam_time = DATE_FORMAT(#{param.examTime}, '%T')
        AND examinee.dept_nm = #{param.deptNm}
        AND examinee.major_nm = #{param.majorNm}
        AND examinee.examinee_nm LIKE '%' #{param.examineeNm} '%'
        AND exam_map.exam_cd = #{param.examCd}
        AND exam_map.virt_no LIKE '%' #{param.virtNo} '%'
        AND exam_map.examinee_cd LIKE '%' #{param.examineeCd} '%'
        AND exam_map.group_nm = #{param.groupNm}
        AND hall.head_nm = #{param.headNm}
        AND hall.bldg_nm = #{param.bldgNm}
        AND hall.hall_nm = #{param.hallNm}

update.if:
UPDATE exam_map
SET eval_cd = #{param.evalCd}
WHERE examinee_cd = #{param.examineeCd} AND exam_cd = #{param.examCd}
